// TTN MQTT payload extraction function for Node-RED
// This function extracts:
//  - Ambient light value (lux) from first 2 bytes (big-endian)
//  - PIR sensor value (0 or 1) from 3rd byte
//  - RSSI and SNR from TTN metadata
//  - Device ID from the TTN message
//  - Location and Sensor Type (hardcoded)

// Step 1: Check and parse the input payload if it's a string
var ttnMsg = msg.payload;
if (typeof ttnMsg === "string") {
    try {
        ttnMsg = JSON.parse(ttnMsg);
    } catch (err) {
        node.error("Invalid JSON payload: " + err);
        return null;
    }
}

// Step 2: Validate required fields
if (!ttnMsg.uplink_message || !ttnMsg.uplink_message.frm_payload) {
    node.error("Missing uplink_message or frm_payload in message");
    return null;
}

// Step 3: Decode the base64 payload into a buffer
var frmPayload = ttnMsg.uplink_message.frm_payload;
var buffer = Buffer.from(frmPayload, 'base64');

// Step 4: Ensure the payload is at least 3 bytes long
if (buffer.length < 3) {
    node.error("Payload is too short. Expected at least 3 bytes.");
    return null;
}

// Step 5: Extract sensor data from buffer
var lux = (buffer[0] << 8) | buffer[1];  // Ambient light in lux (16-bit big-endian)
var pir = buffer[2];                    // PIR sensor state (1 byte)

// Step 6: Attempt to extract RSSI and SNR from metadata
var rssi = null;
var snr = null;

if (Array.isArray(ttnMsg.uplink_message.rx_metadata) && ttnMsg.uplink_message.rx_metadata.length > 0) {
    var metadata = ttnMsg.uplink_message.rx_metadata[0]; // Use first gateway metadata
    rssi = metadata.rssi !== undefined ? metadata.rssi : null;
    snr = metadata.snr !== undefined ? metadata.snr : null;
} else if (ttnMsg.uplink_message.settings) {
    snr = ttnMsg.uplink_message.settings.snr !== undefined ? ttnMsg.uplink_message.settings.snr : null;
}

// Step 7: Extract device ID
var deviceId = ttnMsg.end_device_ids && ttnMsg.end_device_ids.device_id ? ttnMsg.end_device_ids.device_id : null;

// Step 8: Output the extracted values
msg.payload = {
    device_id: deviceId,     // TTN Device ID
    lux: lux,                // Ambient light intensity in lux
    pir: pir,                // PIR sensor reading (0 or 1)
    rssi: rssi,              // Received Signal Strength Indicator
    snr: snr,                // Signal-to-Noise Ratio
    location: 'Punggol',     // Fixed location identifier
    sensor_type: 'lux_pir'   // Sensor type descriptor
    // raw: buffer.toString('hex') // Optional raw payload
};

return msg;

/*
  This Node-RED function extracts sensor data, RSSI, SNR, device_id, and adds fixed metadata like location and sensor type.
  - Decodes the base64-encoded "frm_payload" to extract a sensor value (2-byte big-endian).
  - Extracts RSSI and SNR from the uplink metadata (if available).
  - Adds device_id from TTN message.
  - Adds fixed location ('Punggol') and sensor_type ('moisture').
*/

if (
    msg.payload &&
    msg.payload.uplink_message &&
    msg.payload.uplink_message.frm_payload
) {
    var uplink = msg.payload.uplink_message;

    // Step 1: Decode base64 payload to Buffer
    var buffer = Buffer.from(uplink.frm_payload, 'base64');

    // Step 2: Extract sensor value (first 2 bytes, big-endian)
    var sensorValue = null;
    if (buffer.length >= 2) {
        sensorValue = (buffer[0] << 8) | buffer[1];
    }

    // Step 3: Extract RSSI and SNR from metadata (if available)
    var rssi = null;
    var snr = null;
    if (
        uplink.rx_metadata &&
        uplink.rx_metadata.length > 0
    ) {
        // Use the first gateway's metadata
        var metadata = uplink.rx_metadata[0];
        rssi = metadata.rssi !== undefined ? metadata.rssi : null;
        snr = metadata.snr !== undefined ? metadata.snr : null;
    }

    // Step 4: Extract device_id (if available)
    var deviceId = null;
    if (
        msg.payload.end_device_ids &&
        msg.payload.end_device_ids.device_id
    ) {
        deviceId = msg.payload.end_device_ids.device_id;
    }

    // Step 5: Return structured payload with added metadata
    msg.payload = {
        device_id: deviceId,
        sensor_type: "moisture",
        location: "Punggol",
        moistureSensorValue: sensorValue,
        rssi: rssi,
        snr: snr
    };

} else {
    msg.payload = { error: "Invalid TTN payload structure." };
}

return msg;

/*
   This node reads msg.payload.moistureSensorValue (set by your existing extraction node).
   If moistureSensorValue < 300, it sends the downlink to turn the relay ON ("AQ==").
   Otherwise, it sends the downlink to turn the relay OFF ("AA==").
*/

// Check if moistureSensorValue exists
if (msg.payload && msg.payload.moistureSensorValue !== undefined) {
    let sensorValue = msg.payload.moistureSensorValue;

    if (sensorValue < 300) {
        // Relay ON
        msg.payload.moisture = {
            downlinks: [
                {
                    f_port: 1,
                    frm_payload: "AQ==", // Base64 for ON
                    priority: "HIGH"
                }
            ]
        };
    } else {
        // Relay OFF
        msg.payload.moisture = {
            downlinks: [
                {
                    f_port: 1,
                    frm_payload: "AA==", // Base64 for OFF
                    priority: "HIGH"
                }
            ]
        };
    }
} else {
    // Handle error: sensor value not found
    //msg.payload.moisture = { error: "No moistureSensorValue found in msg.payload." };
    return null;
}

return msg;

// Retrieve the previous PIR state from context (default to 0 if not set)
var prevPir = context.get('prevPir') || 0;

// Extract the current PIR value from the incoming payload
var currentPir = (msg.payload && typeof msg.payload.pir !== 'undefined') ? msg.payload.pir : 0;

// Only send a message if the PIR value changes from 0 to 1
if (prevPir === 0 && currentPir === 1) {
    // Update context with the current state
    context.set('prevPir', currentPir);

    // Build the Telegram-compatible payload
    msg.payload = {
        chatId: -1002694024012,  // Example chat ID
        type: "message",
        content: "ðŸš¨ PIR Motion Detected Object!"
    };
    // Remove any extraneous options property to prevent conflicts
    delete msg.options;
    return msg;
} else {
    // Update the stored state even if no message is sent
    context.set('prevPir', currentPir);
    return null;
}

// Ensure payload exists and contains a valid downlink array
if (msg.payload && msg.payload.downlinks && msg.payload.downlinks.length > 0) {
    // Extract the relay command from the first downlink item
    let relayPayload = msg.payload.downlinks[0].frm_payload;
    let currentState;

    // Determine current state from payload
    if (relayPayload === "AQ==") { // Relay is ON
        currentState = "ON";
    } else if (relayPayload === "AA==") { // Relay is OFF
        currentState = "OFF";
    } else {
        // If the frm_payload is not recognized, drop the message or handle error
        return null;
    }

    // Retrieve the previous state from context (node-level storage)
    let previousState = context.get("previousState");

    // If previous state is not set (first run), initialize it without sending a message
    if (previousState === undefined) {
        context.set("previousState", currentState);
        return null;
    }

    // If the current state is the same as the previous, do nothing (no state change)
    if (currentState === previousState) {
        return null;
    }

    // A state change occurred; update the stored previous state
    context.set("previousState", currentState);

    // Prepare the Telegram message payload based on the new state with different emojis
    if (currentState === "ON") {
        msg.payload = {
            chatId: -1002694024012,  // Use your specific Telegram chat ID here
            type: "message",
            content: "Pump Relay On ðŸš°"
        };
    } else if (currentState === "OFF") {
        msg.payload = {
            chatId: -1002694024012,  // Use your specific Telegram chat ID here
            type: "message",
            content: "Pump Relay Off ðŸš±"
        };
    }

    // Clean up any extraneous properties that might conflict with the Telegram node
    delete msg.options;
    return msg;
} else {
    // If no valid payload is found, drop the message
    return null;
}

// Step 1: Check if payload exists
if (!msg.payload) {
    node.error("No payload found");
    return null;
}

// Step 2: Extract incoming sensor data (assumes fields: 'lux', 'pir', 'device_id', 'location', 'sensor_type', 'rssi', 'snr')
var sensorData = msg.payload;

// Step 3: Define the measurement name for InfluxDB
msg.measurement = "sensor_data";

// Step 4: Structure the fields for InfluxDB (key-value pairs that represent sensor measurements)
// These are values that change frequently and are stored as numeric data
msg.payload = {
    lux: sensorData.lux,
    pir: sensorData.pir,
    rssi: sensorData.rssi,
    snr: sensorData.snr
};

// Step 5: Define the tags for InfluxDB (static descriptors used for indexing and filtering)
// Tags should be strings and low-cardinality
msg.tags = {
    device_id: sensorData.device_id || "unknown_device",
    location: sensorData.location || "unknown_location",
    sensor_type: sensorData.sensor_type || "unknown_sensor"
};

// Step 6: Add a timestamp (optional, if not set InfluxDB will generate one automatically)
msg.timestamp = new Date();

// node.warn(msg);    // Logs only the msg that will be sent to InfluxDB

// Step 7: Return the modified message object to InfluxDB output
return msg;

// Step 1: Check if payload exists and doesn't already contain an error
if (!msg.payload || msg.payload.error) {
    node.error("Invalid or missing sensor data: " + (msg.payload ? msg.payload.error : "No payload"));
    return null;
}

// Step 2: Extract values from the payload
var sensorValue = msg.payload.moistureSensorValue;
var rssi = msg.payload.rssi;
var snr = msg.payload.snr;
var deviceId = msg.payload.device_id;
var location = msg.payload.location;
var sensorType = msg.payload.sensor_type;

// Step 3: Validate that sensorValue, rssi, and snr are numbers
if (typeof sensorValue !== 'number') {
    node.error("Sensor value is not a number.");
    return null;
}
if (typeof rssi !== 'number') {
    node.error("RSSI is not a number.");
    return null;
}
if (typeof snr !== 'number') {
    node.error("SNR is not a number.");
    return null;
}

// Step 4: Set the InfluxDB measurement name
msg.measurement = "sensor_data";

// Step 5: Set InfluxDB tags
msg.tags = {
    device_id: deviceId || "unknown_device",
    location: location || "unknown_location",
    sensor_type: sensorType || "moisture_sensor"
};

// Step 6: Set InfluxDB fields (the actual data values to be stored)
msg.payload = {
    moistureSensorValue: sensorValue,
    rssi: rssi,
    snr: snr
};

//node.warn(msg);    // Logs only the msg that will be sent to InfluxDB

// Step 7: Set optional timestamp
msg.timestamp = new Date();

return msg;

// Check if sensor payload from the first node exists
if (
    msg.payload.moisture &&
    msg.payload.moisture.downlinks &&
    msg.payload.moisture.downlinks[0] &&
    msg.payload.moisture.downlinks[0].frm_payload
) {
    let sensorCommand = msg.payload.moisture.downlinks[0].frm_payload;

    if (sensorCommand === "AA==") {
        // Sensor commands relay OFF; override and send relay OFF.
        msg.payload = {
            downlinks: [
                {
                    f_port: 1,
                    frm_payload: "AA==", // Relay OFF
                    priority: "HIGH"
                }
            ]
        };
        return msg;
    } else if (sensorCommand === "AQ==") {
        // Sensor commands relay ON; do nothing further and return null.
        return null;
    }
}

// If no sensor payload is provided, then process forecast data:
if (
    msg.payload &&
    msg.payload.data &&
    msg.payload.data.items &&
    Array.isArray(msg.payload.data.items) &&
    msg.payload.data.items.length > 0
) {
    // Assume the first item contains forecast info
    let forecasts = msg.payload.data.items[0].forecasts;
    let punggolForecast = null;

    // Look for forecast for Punggol
    for (let i = 0; i < forecasts.length; i++) {
        if (forecasts[i].area === "Punggol") {
            punggolForecast = forecasts[i].forecast;
            break;
        }
    }

    if (punggolForecast) {
        // Check forecast text for "rain" or "showers" (case-insensitive)
        let forecastLower = punggolForecast.toLowerCase();
        if (forecastLower.includes("rain") || forecastLower.includes("showers")) {
            // Forecast indicates rain: turn relay OFF.
            msg.payload = {
                downlinks: [
                    {
                        f_port: 1,
                        frm_payload: "AA==", // Relay OFF
                        priority: "HIGH"
                    }
                ]
            };
        } else {
            // Otherwise, turn relay ON.
            msg.payload = {
                downlinks: [
                    {
                        f_port: 1,
                        frm_payload: "AQ==", // Relay ON
                        priority: "HIGH"
                    }
                ]
            };
        }
    } else {
        // No forecast available for Punggol.
        msg.payload = { error: "No forecast found for area Punggol." };
    }
} else {
    // Expected forecast data structure is missing.
    msg.payload = { error: "No forecast data found in msg.payload." };
}

return msg;

/*
   This function checks the output of the previous relay control node.
   If Relay is ON (frm_payload "AQ=="), it triggers the NEA API HTTP GET by setting the payload to the current date.
   If Relay is OFF (frm_payload "AA=="), it does nothing.
*/

if (msg.payload.moisture && msg.payload.moisture.downlinks && msg.payload.moisture.downlinks.length > 0) {
    let relayState = msg.payload.moisture.downlinks[0].frm_payload;

    if (relayState === "AQ==") {
        // Relay is ON: set payload to current date/time to trigger the HTTP GET request.
        msg.payload = new Date();  // current date/time as a Date object
        return msg;
    } else {
        // Relay is OFF: do nothing.
        return null;
    }
} else {
    // If the expected structure is missing, do nothing.
    return null;
}

// Ensure the payload exists and contains a valid downlinks array
if (msg.payload && Array.isArray(msg.payload.downlinks) && msg.payload.downlinks.length > 0) {
    // Step 1: Extract the relay command from the first downlink item's frm_payload
    let relayPayload = msg.payload.downlinks[0].frm_payload;
    let currentState;

    // Step 2: Determine the current relay state from the base64 payload
    if (relayPayload === "AQ==") {
        currentState = "ON"; // Relay is ON
    } else if (relayPayload === "AA==") {
        currentState = "OFF"; // Relay is OFF
    } else {
        // Step 3: If the payload is unrecognized, discard the message
        return null;
    }

    // Step 4: Prepare the payload for InfluxDB with measurement and tags
    msg.measurement = "irrigation_event"; // InfluxDB measurement name
    msg.payload = {
        relayState: currentState
    };

    // Step 5: Add tags, including the location
    msg.tags = {
        location: "Punggol"
    };

    // Step 6: Optionally include a timestamp (used by InfluxDB)
    msg.timestamp = new Date();

    return msg;
} else {
    // No valid payload found, discard the message
    return null;
}

// Step 1: Validate payload existence and check for error
if (!msg.payload || msg.payload.error) {
    node.error("Invalid or missing forecast data: " + (msg.payload ? msg.payload.error : "No payload"));
    return null;
}

// Step 2: Ensure forecast data structure is valid
const items = msg.payload.data?.items;
if (!items || !Array.isArray(items) || items.length === 0) {
    node.error("Forecast data structure is missing or empty.");
    return null;
}

// Step 3: Extract forecasts array
const forecasts = items[0].forecasts;
if (!forecasts || !Array.isArray(forecasts)) {
    node.error("Forecasts array is missing or not an array.");
    return null;
}

// Step 4: Search for Punggol forecast
let weatherForecast = null;
for (let i = 0; i < forecasts.length; i++) {
    if (forecasts[i].area === "Punggol") {
        weatherForecast = forecasts[i].forecast;
        break;
    }
}

// Step 5: Validate forecast result
if (typeof weatherForecast !== 'string') {
    node.error("Forecast for Punggol not found or invalid.");
    return null;
}

// Step 6: Prepare payload for InfluxDB
msg.measurement = "irrigation_event";
msg.payload = {
    weather_forecast: weatherForecast
};

// Step 7: Add tags (location: Punggol) and timestamp
msg.tags = {
    location: "Punggol"
};
msg.timestamp = new Date();  // Optional: InfluxDB uses this as measurement time

// Step 8: Return modified message object
return msg;

/*
   This function calculates water cost based on the irrigation duration (relay ON time).
   It uses a fixed water tariff of SGD $1.21/mÂ³ and an assumed flow rate of 0.1 mÂ³/min.
   When a relay ON command ("AQ==") is received, it stores the timestamp (if not already set).
   When a relay OFF command ("AA==") is received, it calculates the duration, water volume, and cost.

   NOTE: We use getTime() on Date objects to avoid TypeScript errors during date arithmetic.
*/

// Validate payload structure
if (!msg.payload || !msg.payload.downlinks || !Array.isArray(msg.payload.downlinks) || msg.payload.downlinks.length === 0) {
    node.error("Missing or invalid downlinks in payload.");
    return null;
}

// Extract relay command from the first downlink item
var relayCmd = msg.payload.downlinks[0].frm_payload;

// Retrieve stored relay ON time from context
var relayOnTime = context.get('relayOnTime');

if (relayCmd === "AQ==") {
    // Relay is turning ON: store the current timestamp only if not already stored
    if (!relayOnTime) {
        context.set('relayOnTime', new Date());
    }
    // No calculation yet
    return null;

} else if (relayCmd === "AA==") {
    // Relay is turning OFF: proceed to calculate duration and water cost
    if (!relayOnTime) {
        node.error("Relay OFF received without a recorded relay ON time.");
        return null;
    }

    // Convert both timestamps to numeric values for calculation
    var nowTimestamp = new Date().getTime();
    var onTimestamp = new Date(relayOnTime).getTime();
    var durationMs = nowTimestamp - onTimestamp;
    var durationMinutes = durationMs / 60000; // Convert to minutes

    // Constants
    var flowRate = 0.1; // in mÂ³/min
    var waterRate = 3.24; // SGD per mÂ³

    // Compute water volume and cost
    var waterVolume = durationMinutes * flowRate;
    var waterCost = waterVolume * waterRate;

    // Format message payload for InfluxDB
    msg.measurement = "irrigation_event";
    msg.payload = {
        waterCost: waterCost,
        irrigationDurationMinutes: durationMinutes
    };
    msg.tags = {
        location: "Punggol"
    };
    msg.timestamp = new Date();

    // Clear the stored ON time
    context.set('relayOnTime', null);

    node.warn(msg);    // Logs only the msg that will be sent to InfluxDB

    return msg;

} else {
    node.error("Unrecognized relay command: " + relayCmd);
    return null;
}

/*
  Moisture Sensor Watchdog Timer Function
  -----------------------------------------
  This function continuously monitors for uplink messages from the Moisture Sensor.
  - On each incoming message it clears any existing timer and starts a fresh one.
  - If no message is received for 40 seconds, it sends a Telegram alert (with emojis) and restarts the timer.

  Wire this node after your Moisture extraction function and connect its output to your Telegram sender.
*/

var sourceName = "Moisture Sensor";  // Identifies the monitored node

// Clear any existing timer
if (context.timer) {
  clearTimeout(context.timer);
}

// Function to start (or restart) the timer
function startTimer() {
  context.timer = setTimeout(function () {
    var alertMsg = {
      payload: {
        chatId: -1002694024012,  // Replace with your actual Telegram chat ID
        type: "message",
        content: "ðŸš¨âš ï¸ Alert: No uplink message received from " + sourceName + " for more than 40 seconds! â°"
      }
    };
    // Send the alert message to the Telegram sender node
    node.send(alertMsg);
    // Restart the timer so alerts continue every 40 seconds if still silent
    startTimer();
  }, 40000);
}

// Start the timer
startTimer();

// Do not pass along the current incoming message
return null;

