// TTN MQTT payload extraction function for Node-RED
// This function assumes the TTN uplink message has the following structure:
// {
//   ...,
//   "uplink_message": {
//       ...,
//       "frm_payload": "ABAA",
//       ...
//   }
// }

// Check if the incoming msg.payload is a string and parse it if needed.
var ttnMsg = msg.payload;
if (typeof ttnMsg === "string") {
    try {
        ttnMsg = JSON.parse(ttnMsg);
    } catch (err) {
        node.error("Invalid JSON payload: " + err);
        return null;
    }
}

// Verify the payload contains the uplink_message and frm_payload properties.
if (!ttnMsg.uplink_message || !ttnMsg.uplink_message.frm_payload) {
    node.error("Missing uplink_message or frm_payload in message");
    return null;
}

// Get the base64 encoded payload.
var frmPayload = ttnMsg.uplink_message.frm_payload;

// Decode the base64 payload into a buffer.
var buffer = Buffer.from(frmPayload, 'base64');

// Ensure the payload has at least 3 bytes (2 for ambient light, 1 for PIR sensor).
if (buffer.length < 3) {
    node.error("Payload is too short. Expected at least 3 bytes.");
    return null;
}

// Extract ambient light value from the first two bytes (big-endian).
var lux = (buffer[0] << 8) | buffer[1];

// Extract PIR sensor value from the third byte.
var pir = buffer[2];

// You can add further processing here if needed.
// Set the decoded data as the new msg.payload.
msg.payload = {
    lux: lux,      // Ambient light intensity in lux
    pir: pir,      // PIR sensor reading (0 or 1)
    // raw: buffer.toString('hex')  // Optional: Raw payload in hex for debugging
};

return msg;

/*
  This Node-RED function extracts sensor data from a TTN MQTT message.
  It decodes the base64-encoded "frm_payload" field and converts the first two bytes
  into a sensor value (assuming a two-byte big-endian encoded value).
*/

if (
    msg.payload &&
    msg.payload.uplink_message &&
    msg.payload.uplink_message.frm_payload
) {
    // Get the base64 encoded payload
    var base64Payload = msg.payload.uplink_message.frm_payload;

    // Decode the base64 payload to a Buffer
    var buffer = Buffer.from(base64Payload, 'base64');

    // Ensure we have at least two bytes of data
    if (buffer.length >= 2) {
        // Combine the two bytes to get the sensor value (big-endian)
        var sensorValue = (buffer[0] << 8) | buffer[1];

        // You can also extract additional fields if needed:
        // var deviceID = msg.payload.end_device_ids.device_id;
        // var receivedAt = msg.payload.received_at;

        // Set the new payload to an object with the extracted sensor value.
        msg.payload = { moistureSensorValue: sensorValue };
    } else {
        // If the payload is shorter than expected
        msg.payload = { error: "Payload too short to extract sensor value." };
    }
} else {
    // If the message structure does not match the expected TTN format
    msg.payload = { error: "Invalid TTN payload structure." };
}

return msg;

/*
   This node reads msg.payload.moistureSensorValue (set by your existing extraction node).
   If moistureSensorValue < 300, it sends the downlink to turn the relay ON ("AQ==").
   Otherwise, it sends the downlink to turn the relay OFF ("AA==").
*/

// Check if moistureSensorValue exists
if (msg.payload && msg.payload.moistureSensorValue !== undefined) {
    let sensorValue = msg.payload.moistureSensorValue;

    if (sensorValue < 300) {
        // Relay ON
        msg.payload.moisture = {
            downlinks: [
                {
                    f_port: 1,
                    frm_payload: "AQ==", // Base64 for ON
                    priority: "HIGH"
                }
            ]
        };
    } else {
        // Relay OFF
        msg.payload.moisture = {
            downlinks: [
                {
                    f_port: 1,
                    frm_payload: "AA==", // Base64 for OFF
                    priority: "HIGH"
                }
            ]
        };
    }
} else {
    // Handle error: sensor value not found
    //msg.payload.moisture = { error: "No moistureSensorValue found in msg.payload." };
    return null;
}

return msg;

// Retrieve the previous PIR state from context (default to 0 if not set)
var prevPir = context.get('prevPir') || 0;

// Extract the current PIR value from the incoming payload
var currentPir = (msg.payload && typeof msg.payload.pir !== 'undefined') ? msg.payload.pir : 0;

// Only send a message if the PIR value changes from 0 to 1
if (prevPir === 0 && currentPir === 1) {
    // Update context with the current state
    context.set('prevPir', currentPir);

    // Build the Telegram-compatible payload
    msg.payload = {
        chatId: -4640844747,  // Example chat ID
        type: "message",
        content: "ðŸš¨ PIR Motion Detected Object!"
    };
    // Remove any extraneous options property to prevent conflicts
    delete msg.options;
    return msg;
} else {
    // Update the stored state even if no message is sent
    context.set('prevPir', currentPir);
    return null;
}

// Ensure payload exists and contains a valid downlink array
if (msg.payload && msg.payload.downlinks && msg.payload.downlinks.length > 0) {
    // Extract the relay command from the first downlink item
    let relayPayload = msg.payload.downlinks[0].frm_payload;
    let currentState;

    // Determine current state from payload
    if (relayPayload === "AQ==") { // Relay is ON
        currentState = "ON";
    } else if (relayPayload === "AA==") { // Relay is OFF
        currentState = "OFF";
    } else {
        // If the frm_payload is not recognized, drop the message or handle error
        return null;
    }

    // Retrieve the previous state from context (node-level storage)
    let previousState = context.get("previousState");

    // If previous state is not set (first run), initialize it without sending a message
    if (previousState === undefined) {
        context.set("previousState", currentState);
        return null;
    }

    // If the current state is the same as the previous, do nothing (no state change)
    if (currentState === previousState) {
        return null;
    }

    // A state change occurred; update the stored previous state
    context.set("previousState", currentState);

    // Prepare the Telegram message payload based on the new state with different emojis
    if (currentState === "ON") {
        msg.payload = {
            chatId: -4640844747,  // Use your specific Telegram chat ID here
            type: "message",
            content: "Pump Relay On ðŸš°"
        };
    } else if (currentState === "OFF") {
        msg.payload = {
            chatId: -4640844747,  // Use your specific Telegram chat ID here
            type: "message",
            content: "Pump Relay Off ðŸš±"
        };
    }

    // Clean up any extraneous properties that might conflict with the Telegram node
    delete msg.options;
    return msg;
} else {
    // If no valid payload is found, drop the message
    return null;
}

// Check if payload exists
if (!msg.payload) {
    node.error("No payload found");
    return null;
}

// Assume the incoming payload has properties: lux and pir
var sensorData = msg.payload;

// Give the measurement name to msg.measurement
msg.measurement = "ttn_sensor";

// Put your sensor fields in msg.payload
msg.payload = {
    lux: sensorData.lux,
    pir: sensorData.pir
};

// Optionally, include a custom timestamp in msg.timestamp
msg.timestamp = new Date();

return msg;

// Check if payload exists and doesn't already contain an error
if (!msg.payload || msg.payload.error) {
    node.error("Invalid or missing moisture sensor data: " + (msg.payload ? msg.payload.error : "No payload"));
    return null;
}

// Retrieve the moisture sensor value from the previous function
var sensorValue = msg.payload.moistureSensorValue;

// Validate that the sensor value is a number
if (typeof sensorValue !== 'number') {
    node.error("Sensor value is not a number.");
    return null;
}

// Set the measurement name for InfluxDB
msg.measurement = "ttn_sensor";

// Format the payload with the sensor value for InfluxDB
msg.payload = {
    moistureSensorValue: sensorValue
};

// Optionally add a timestamp (InfluxDB will use this as the measurement time)
msg.timestamp = new Date();

return msg;

// Check if sensor payload from the first node exists
if (
    msg.payload.moisture &&
    msg.payload.moisture.downlinks &&
    msg.payload.moisture.downlinks[0] &&
    msg.payload.moisture.downlinks[0].frm_payload
) {
    let sensorCommand = msg.payload.moisture.downlinks[0].frm_payload;

    if (sensorCommand === "AA==") {
        // Sensor commands relay OFF; override and send relay OFF.
        msg.payload = {
            downlinks: [
                {
                    f_port: 1,
                    frm_payload: "AA==", // Relay OFF
                    priority: "HIGH"
                }
            ]
        };
        return msg;
    } else if (sensorCommand === "AQ==") {
        // Sensor commands relay ON; do nothing further and return null.
        return null;
    }
}

// If no sensor payload is provided, then process forecast data:
if (
    msg.payload &&
    msg.payload.data &&
    msg.payload.data.items &&
    Array.isArray(msg.payload.data.items) &&
    msg.payload.data.items.length > 0
) {
    // Assume the first item contains forecast info
    let forecasts = msg.payload.data.items[0].forecasts;
    let punggolForecast = null;

    // Look for forecast for Punggol
    for (let i = 0; i < forecasts.length; i++) {
        if (forecasts[i].area === "Punggol") {
            punggolForecast = forecasts[i].forecast;
            break;
        }
    }

    if (punggolForecast) {
        // Check forecast text for "rain" or "showers" (case-insensitive)
        let forecastLower = punggolForecast.toLowerCase();
        if (forecastLower.includes("rain") || forecastLower.includes("showers")) {
            // Forecast indicates rain: turn relay OFF.
            msg.payload = {
                downlinks: [
                    {
                        f_port: 1,
                        frm_payload: "AA==", // Relay OFF
                        priority: "HIGH"
                    }
                ]
            };
        } else {
            // Otherwise, turn relay ON.
            msg.payload = {
                downlinks: [
                    {
                        f_port: 1,
                        frm_payload: "AQ==", // Relay ON
                        priority: "HIGH"
                    }
                ]
            };
        }
    } else {
        // No forecast available for Punggol.
        msg.payload = { error: "No forecast found for area Punggol." };
    }
} else {
    // Expected forecast data structure is missing.
    msg.payload = { error: "No forecast data found in msg.payload." };
}

return msg;

/*
   This function checks the output of the previous relay control node.
   If Relay is ON (frm_payload "AQ=="), it triggers the NEA API HTTP GET by setting the payload to the current date.
   If Relay is OFF (frm_payload "AA=="), it does nothing.
*/

if (msg.payload.moisture && msg.payload.moisture.downlinks && msg.payload.moisture.downlinks.length > 0) {
    let relayState = msg.payload.moisture.downlinks[0].frm_payload;

    if (relayState === "AQ==") {
        // Relay is ON: set payload to current date/time to trigger the HTTP GET request.
        msg.payload = new Date();  // current date/time as a Date object
        return msg;
    } else {
        // Relay is OFF: do nothing.
        return null;
    }
} else {
    // If the expected structure is missing, do nothing.
    return null;
}

// Ensure payload exists and contains a valid downlink array
if (msg.payload && msg.payload.downlinks && msg.payload.downlinks.length > 0) {
    // Extract the relay command from the first downlink item
    let relayPayload = msg.payload.downlinks[0].frm_payload;
    let currentState;

    // Determine current state from payload
    if (relayPayload === "AQ==") { // Relay is ON
        currentState = "ON";
    } else if (relayPayload === "AA==") { // Relay is OFF
        currentState = "OFF";
    } else {
        // If the frm_payload is not recognized, drop the message or handle error
        return null;
    }

    // Retrieve the previous state from context (node-level storage)
    let previousState = context.get("previousState");

    // If previous state is not set (first run), initialize it without sending a message
    if (previousState === undefined) {
        context.set("previousState", currentState);
        return null;
    }

    // If the current state is the same as the previous, do nothing (no state change)
    if (currentState === previousState) {
        return null;
    }

    // A state change occurred; update the stored previous state
    context.set("previousState", currentState);

    // Prepare the InfluxDB payload with the new relay state
    msg.measurement = "ttn_sensor"; // Set the measurement name for InfluxDB
    msg.payload = {
        relayState: currentState
    };
    // Optionally add a timestamp (InfluxDB will use this as the measurement time)
    msg.timestamp = new Date();

    return msg;
} else {
    // If no valid payload is found, drop the message
    return null;
}

// Check if payload exists and doesn't already contain an error
if (!msg.payload || msg.payload.error) {
    node.error("Invalid or missing forecast data: " + (msg.payload ? msg.payload.error : "No payload"));
    return null;
}

// Verify the expected forecast data structure exists
if (!msg.payload.data || !msg.payload.data.items || !Array.isArray(msg.payload.data.items) || msg.payload.data.items.length === 0) {
    node.error("Forecast data structure is missing or empty.");
    return null;
}

// Retrieve the forecasts array from the first item
var forecasts = msg.payload.data.items[0].forecasts;
if (!forecasts || !Array.isArray(forecasts)) {
    node.error("Forecasts array is missing or not an array.");
    return null;
}

// Find the forecast for the "Punggol" area
var punggolForecast = null;
for (var i = 0; i < forecasts.length; i++) {
    if (forecasts[i].area === "Punggol") {
        punggolForecast = forecasts[i].forecast;
        break;
    }
}

// Validate that a forecast was found and is a string
if (typeof punggolForecast !== 'string') {
    node.error("Forecast for Punggol not found or invalid.");
    return null;
}

// Set the measurement name for InfluxDB
msg.measurement = "ttn_sensor";

// Format the payload for InfluxDB with the Punggol forecast
msg.payload = {
    punggolForecast: punggolForecast
};

// Optionally add a timestamp (InfluxDB will use this as the measurement time)
msg.timestamp = new Date();

return msg;
